import "../styles/createPost.css";
import React, { useState, useContext } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import AuthContext from "../context/authContext";

const CreatePost = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(""); // Define image state
  const [author, setAuthor] = useState(""); // Define author state
  const [description, setDescription] = useState(""); // Define description state
  const [message, setMessage] = useState(""); // Define a state for success/error message
  const [error, setError] = useState("");
  const { token } = useContext(AuthContext);

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Post</h2>
      {error && <p>{error}</p>}

      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />

      <textarea
        placeholder="Content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        required
      />

      {/* Input for image URL */}
      <input
        type="text"
        placeholder="Image URL"
        value={image}
        onChange={(e) => setImage(e.target.value)}
      />

      {/* Input for author */}

      <button type="submit">Create Post</button>
    </form>
  );
};

export default CreatePost;

