import React from "react";
import { Link } from "react-router-dom";
import BlogList from "../components/BlogList";
import "../styles/postCard.css";

const BlogCard = ({
  id,
  title,
  image,
  author,
  authorImage,
  date,
  description,
  tags,
}) => {
  return (
    <div className="blog-card">
      <img src={image} alt={title} />
      <div className="content">
        <h2>{title}</h2>
        <div className="author-info">
          {authorImage && (
            <img src={authorImage} alt={author.name || "Unknown Author"} />
          )}{" "}
          {/* author.name */}
          <p>
            by <strong>{author ? author.name : "Unknown Author"}</strong>{" "}
            {/* author.name */}
            on {new Date(date).toLocaleDateString()}
          </p>
        </div>
        <p>{description}</p>

        {/* Use Link for navigation to blog detail page */}
        <Link to={`/blogs/${id}`} className="read-more">
          Read More →
        </Link>

        {tags && tags.length > 0 && (
          <div className="tags">
            {tags.map((tag, index) => (
              <span key={index}>{tag}</span>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};
