import "../styles/postCard.css";

const PostCard = () => {
  const [blogPosts, setBlogPosts] = useState([]); // State to store API data
  const [loading, setLoading] = useState(true); // State to show loading indicator
  const [error, setError] = useState(null); // State to handle errors

  // Fetch data from the API when the component mounts
  useEffect(() => {
    const fetchBlogPosts = async () => {
      try {
        const response = await fetch("https://api.example.com/blogposts"); // Replace with your API URL
        if (!response.ok) {
          throw new Error("Failed to fetch blog posts");
        }
        const data = await response.json();
        setBlogPosts(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBlogPosts();
  }, []); // Empty dependency array to fetch data only once

  if (loading) return <div>Loading...</div>; // Show loading message while fetching data
  if (error) return <div>Error: {error}</div>; // Show error message if there's an error

  return (
    <div className="blog-list">
      {blogPosts.map((post, index) => (
        <BlogCard
          key={index}
          title={post.title}
          author={post.author}
          date={post.date}
          description={post.description}
          image={post.image}
          authorImage={post.authorImage}
          tags={post.tags}
        />
      ))}
    </div>
  );
};

