import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import styles from "./BlogDetail.module.css"; // Adjust path to your CSS module

const BlogDetail = () => {
  const { id } = useParams(); // Get blog ID from the URL
  const [blog, setBlog] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/blogs/${id}`
        );
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching blog post", error);
      }
    };

    fetchBlog();
  }, [id]);

  if (!blog) {
    return <p>Loading...</p>;
  }

  return (
    <div className={styles.blogContainer}>
      {/* Title */}
      <h1 className={styles.title}>{blog.title}</h1>

      {/* Author and Date */}
      <div className={styles.authorInfo}>
        <span className={styles.author}>By {blog.author}</span>
        <span className={styles.date}>
          {new Date(blog.date).toLocaleDateString()} {/* Format date */}
        </span>
      </div>

      {/* Blog Content */}
      <div className={styles.blogContent}>
        {blog.content.split("\n").map((para, index) => (
          <p key={index}>{para}</p>
        ))}
      </div>

      {/* Interactive Icons */}
      <div className={styles.interactions}>
        <span className={styles.icon}>üëç</span>
        <span className={styles.icon}>üëé</span>
        <span className={styles.icon}>üí¨</span>
      </div>
    </div>
  );
};

export default BlogDetail;
