function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Header />
              <BlogList />
            </>
          }
        />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/create" element={<CreatePost />} />
        <Route path="/createpost" element={<CreatePost />} />
        <Route path="/blogdetail" element={<BlogDetail />} />
      </Routes>
    </Router>
  );
}

export default App;
