import { createContext, useState, useEffect } from "react";
import axios from "axios";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state to prevent early redirects

  useEffect(() => {
    const fetchUserData = async () => {
      if (!token) return;
      try {
        const response = await axios.get("http://localhost:5000/api/auth/me", {
          headers: {
            "x-auth-token": token, // Send token as 'x-auth-token'
          },
        });
        setUser(response.data);
        setLoading(false); // Set loading to false after fetching user data
      } catch (error) {
        console.error("Error fetching user data:", error);
        setUser(null);
        setLoading(false); // Prevent loading spinner if error occurs
      }
    };

    fetchUserData();
  }, [token]);

  return (
    <AuthContext.Provider value={{ user, token, setToken, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
