import React, { useEffect, useState } from "react";
import PostCard from "./PostCard";

const BlogList = () => {
  const [blogPosts, setBlogPosts] = useState([]); // State to store API data
  const [loading, setLoading] = useState(true); // State to show loading indicator
  const [error, setError] = useState(null); // State to handle errors

  // Fetch data from the API (your backend) when the component mounts
  useEffect(() => {
    const fetchBlogPosts = async () => {
      try {
        const response = await fetch("/api/posts"); // Replace with your API URL
        if (!response.ok) {
          throw new Error("Failed to fetch blog posts");
        }
        const data = await response.json();
        setBlogPosts(data); // Assuming the response is an array of blog posts
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBlogPosts();
  }, []); // Empty dependency array to fetch data only once

  if (loading) return <div>Loading...</div>; // Show loading message while fetching data
  if (error) return <div>Error: {error}</div>; // Show error message if there's an error
  return (
    <div>
      <h1>All Blog Posts</h1>
      {error && <p>{error}</p>}
      <ul>
        {blogPosts.map((post) => (
          <li key={post._id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

