import { useContext } from "react";
import { Navigate } from "react-router-dom";
import AuthContext from "../context/authContext"; // Import the AuthContext (adjust the path if needed)

const ProtectedRoute = ({ children }) => {
  const isAuthenticated = !!localStorage.getItem("token");
  const { token, loading } = useContext(AuthContext);

  // If still loading the authentication state, don't render anything yet
  if (loading) {
    return <div>Loading...</div>; // Or you can return a spinner or skeleton screen
  }

  // If the user is not authenticated, redirect to login page
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  // If authenticated, render the child components
  return children;
};

export default ProtectedRoute;
